import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@ohrm/core/util/services/api.service';
import useDateFormat from '@/core/util/composable/useDateFormat';
import usePaginate from '@ohrm/core/util/composable/usePaginate';
import { formatTime, parseTime } from '@/core/util/helper/datefns';
import DeleteConfirmationDialog from '@ohrm/components/dialogs/DeleteConfirmationDialog';
export default {
  components: {
    'delete-confirmation': DeleteConfirmationDialog
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/work-shifts');
    const {
      timeFormat,
      jsTimeFormat
    } = useDateFormat();
    const workShiftNormalizer = data => {
      return data.map(item => {
        return {
          id: item.id,
          name: item.name,
          startTime: formatTime(parseTime(item.startTime, timeFormat), jsTimeFormat),
          endTime: formatTime(parseTime(item.endTime, timeFormat), jsTimeFormat),
          hoursPerDay: parseFloat(item.hoursPerDay).toFixed(2)
        };
      });
    };
    const {
      showPaginator,
      currentPage,
      total,
      pages,
      pageSize,
      response,
      isLoading,
      execQuery
    } = usePaginate(http, {
      normalizer: workShiftNormalizer
    });
    return {
      http,
      showPaginator,
      currentPage,
      isLoading,
      total,
      pages,
      pageSize,
      execQuery,
      items: response
    };
  },
  data() {
    return {
      headers: [{
        name: 'name',
        slot: 'title',
        title: this.$t('general.name'),
        style: {
          flex: 1
        }
      }, {
        name: 'startTime',
        title: this.$t('general.from'),
        style: {
          flex: 1
        }
      }, {
        name: 'endTime',
        title: this.$t('general.to'),
        style: {
          flex: 1
        }
      }, {
        name: 'hoursPerDay',
        title: this.$t('admin.hours_per_day'),
        style: {
          flex: 1
        }
      }, {
        name: 'actions',
        title: this.$t('general.actions'),
        slot: 'action',
        style: {
          flex: 1
        },
        cellType: 'oxd-table-cell-actions',
        cellConfig: {
          delete: {
            onClick: this.onClickDelete,
            component: 'oxd-icon-button',
            props: {
              name: 'trash'
            }
          },
          edit: {
            onClick: this.onClickEdit,
            props: {
              name: 'pencil-fill'
            }
          }
        }
      }],
      checkedItems: []
    };
  },
  methods: {
    onClickAdd() {
      navigate('/admin/saveWorkShifts');
    },
    onClickEdit(item) {
      navigate('/admin/saveWorkShifts/{id}', {
        id: item.id
      });
    },
    onClickDeleteSelected() {
      const ids = this.checkedItems.map(index => {
        return this.items?.data[index].id;
      });
      this.$refs.deleteDialog.showDialog().then(confirmation => {
        if (confirmation === 'ok') {
          this.deleteItems(ids);
        }
      });
    },
    onClickDelete(item) {
      this.$refs.deleteDialog.showDialog().then(confirmation => {
        if (confirmation === 'ok') {
          this.deleteItems([item.id]);
        }
      });
    },
    deleteItems(items) {
      if (items instanceof Array) {
        this.isLoading = true;
        this.http.deleteAll({
          ids: items
        }).then(() => {
          return this.$toast.deleteSuccess();
        }).then(() => {
          this.isLoading = false;
          this.resetDataTable();
        });
      }
    },
    async resetDataTable() {
      this.checkedItems = [];
      await this.execQuery();
    }
  }
};
