import { urlFor } from '@ohrm/core/util/helper/url';
import { required } from '@ohrm/core/util/validation/rules';
import { navigate, reloadPage } from '@ohrm/core/util/helper/navigation';
import LoginLayout from '../components/LoginLayout';
import { OxdAlert, OxdIcon, OxdSheet } from '@ohrm/oxd';
export default {
  components: {
    'oxd-icon': OxdIcon,
    'oxd-alert': OxdAlert,
    'oxd-sheet': OxdSheet,
    'login-layout': LoginLayout
  },
  props: {
    error: {
      type: Object,
      default: () => null
    },
    token: {
      type: String,
      required: true
    },
    showSocialMedia: {
      type: Boolean,
      default: true
    },
    isDemoMode: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      username: '',
      password: '',
      rules: {
        username: [required],
        password: [required]
      },
      submitted: false
    };
  },
  computed: {
    submitUrl() {
      return urlFor('/auth/validate');
    }
  },
  beforeMount() {
    setTimeout(() => {
      reloadPage();
    }, 1200000); // 20 * 60 * 1000 (20 minutes);
  },

  methods: {
    onSubmit() {
      if (!this.submitted) {
        this.submitted = true;
        this.$refs.loginForm.$el.submit();
      }
    },
    navigateUrl() {
      navigate('/auth/requestPasswordResetCode');
    }
  }
};
