import { computed } from 'vue';
import useLocale from '@/core/util/composable/useLocale';
import useDateFormat from '@/core/util/composable/useDateFormat';
import { formatDate, parseDate } from '@/core/util/helper/datefns';
import ProfileImage from '@/orangehrmBuzzPlugin/components/ProfileImage';
import useEmployeeNameTranslate from '@/core/util/composable/useEmployeeNameTranslate';
import { OxdDropdownMenu, OxdIcon, OxdSheet } from '@ohrm/oxd';
export default {
  name: 'PostContainer',
  components: {
    'oxd-icon': OxdIcon,
    'oxd-sheet': OxdSheet,
    'oxd-dropdown': OxdDropdownMenu,
    'profile-image': ProfileImage
  },
  props: {
    post: {
      type: Object,
      required: true
    }
  },
  emits: ['edit', 'delete'],
  setup(props) {
    const {
      locale
    } = useLocale();
    const {
      jsDateFormat,
      jsTimeFormat
    } = useDateFormat();
    const {
      $tEmpName
    } = useEmployeeNameTranslate();
    const employeeFullName = computed(() => {
      return $tEmpName(props.post.employee, {
        includeMiddle: true,
        excludePastEmpTag: false
      });
    });
    const postDateTime = computed(() => {
      const {
        createdDate,
        createdTime
      } = props.post;
      const utcDate = parseDate(`${createdDate} ${createdTime} +00:00`, 'yyyy-MM-dd HH:mm xxx');
      return formatDate(utcDate, `${jsDateFormat} ${jsTimeFormat}`, {
        locale
      });
    });
    return {
      postDateTime,
      employeeFullName
    };
  }
};
