import "core-js/modules/es.array.push.js";
import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@/core/util/services/api.service';
import { required, shouldNotExceedCharLength, validPhoneNumberFormat } from '@ohrm/core/util/validation/rules';
const initialLocation = {
  name: '',
  countryCode: null,
  province: '',
  city: '',
  address: '',
  zipCode: '',
  phone: '',
  fax: '',
  note: ''
};
export default {
  props: {
    countries: {
      type: Array,
      default: () => []
    }
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/locations');
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      location: {
        ...initialLocation
      },
      rules: {
        name: [required, shouldNotExceedCharLength(100)],
        countryCode: [required],
        province: [shouldNotExceedCharLength(50)],
        city: [shouldNotExceedCharLength(50)],
        address: [shouldNotExceedCharLength(250)],
        zipCode: [shouldNotExceedCharLength(30)],
        phone: [shouldNotExceedCharLength(30), validPhoneNumberFormat],
        fax: [shouldNotExceedCharLength(30), validPhoneNumberFormat],
        note: [shouldNotExceedCharLength(250)]
      }
    };
  },
  computed: {
    hasCreatePermissions() {
      return this.$can.create(`locations`);
    }
  },
  created() {
    this.isLoading = true;
    this.http.getAll({
      limit: 0
    }).then(response => {
      const {
        data
      } = response.data;
      this.rules.name.push(v => {
        const index = data.findIndex(item => String(item.name).toLowerCase() == String(v).toLowerCase());
        return index === -1 || this.$t('general.already_exists');
      });
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onCancel() {
      navigate('/admin/viewLocations');
    },
    onSave() {
      this.isLoading = true;
      this.http.create({
        ...this.location,
        countryCode: this.location.countryCode.id
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.onCancel();
      });
    }
  }
};
