import ProfilePicture from '@/orangehrmCorporateDirectoryPlugin/components/ProfilePicture';
import { OxdIcon, OxdSheet } from '@ohrm/oxd';
export default {
  name: 'SummaryCard',
  components: {
    'oxd-icon': OxdIcon,
    'oxd-sheet': OxdSheet,
    'profile-picture': ProfilePicture
  },
  props: {
    employeeId: {
      type: Number,
      required: true
    },
    employeeName: {
      type: String,
      required: true
    },
    employeeDesignation: {
      type: String,
      required: false,
      default: null
    },
    employeeSubUnit: {
      type: String,
      default: ''
    },
    employeeLocation: {
      type: String,
      default: ''
    },
    showBackButton: {
      type: Boolean,
      default: false
    }
  },
  emits: ['hide-details'],
  computed: {
    hasDefaultSlot() {
      return !!this.$slots.default;
    },
    cardTitleClasses() {
      return {
        'orangehrm-directory-card-header': true,
        '--break-words': !this.hasDefaultSlot
      };
    },
    cardSubTitleClasses() {
      return {
        'orangehrm-directory-card-subtitle': true,
        '--break-words': !this.hasDefaultSlot
      };
    },
    cardDescriptionClasses() {
      return {
        'orangehrm-directory-card-description': true,
        '--break-words': !this.hasDefaultSlot
      };
    }
  }
};
