import "core-js/modules/es.array.push.js";
import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@ohrm/core/util/services/api.service';
import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
export default {
  props: {
    qualificationLanguageId: {
      type: Number,
      required: true
    }
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/languages');
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      language: {
        id: '',
        name: ''
      },
      rules: {
        name: [required, shouldNotExceedCharLength(120)]
      }
    };
  },
  created() {
    this.isLoading = true;
    this.http.get(this.qualificationLanguageId).then(response => {
      const {
        data
      } = response.data;
      this.language.id = data.id;
      this.language.name = data.name;
      // Fetch list data for unique test
      return this.http.getAll();
    }).then(response => {
      const {
        data
      } = response.data;
      this.rules.name.push(v => {
        const index = data.findIndex(item => String(item.name).toLowerCase() == String(v).toLowerCase());
        if (index > -1) {
          const {
            id
          } = data[index];
          return id !== this.language.id ? this.$t('general.already_exists') : true;
        } else {
          return true;
        }
      });
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.update(this.qualificationLanguageId, {
        name: this.language.name
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      navigate('/admin/viewLanguages');
    }
  }
};
