import { APIService } from '@ohrm/core/util/services/api.service';
import { OxdSwitchInput } from '@ohrm/oxd';
const optionalFieldModel = {
  pimShowDeprecatedFields: false,
  showSSN: false,
  showSIN: false,
  showTaxExemptions: false
};
export default {
  components: {
    'oxd-switch-input': OxdSwitchInput
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/pim/optional-field');
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      optionalField: {
        ...optionalFieldModel
      }
    };
  },
  created() {
    this.isLoading = true;
    this.http.getAll().then(response => {
      const {
        data
      } = response.data;
      this.optionalField = {
        ...data
      };
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.request({
        method: 'PUT',
        data: {
          ...this.optionalField
        }
      }).then(response => {
        const {
          data
        } = response.data;
        this.optionalField = {
          ...data
        };
        this.$toast.saveSuccess();
        this.isLoading = false;
      });
    }
  }
};
