import { computed } from 'vue';
const defaultPic = `${window.appGlobal.publicPath}/images/default-photo.png`;
export default {
  name: 'ProfilePicture',
  props: {
    id: {
      type: Number,
      required: true
    }
  },
  setup(props) {
    const imgSrc = computed(() => {
      return props.id ? `${window.appGlobal.baseUrl}/pim/viewPhoto/empNumber/${props.id}` : defaultPic;
    });
    return {
      imgSrc
    };
  }
};
