import { shouldNotExceedCharLength, digitsOnly, validDateFormat, endDateShouldBeAfterStartDate } from '@ohrm/core/util/validation/rules';
import useDateFormat from '@/core/util/composable/useDateFormat';
const educationModel = {
  name: '',
  institute: '',
  major: '',
  year: '',
  score: '',
  startDate: '',
  endDate: ''
};
export default {
  name: 'EditEducation',
  props: {
    http: {
      type: Object,
      required: true
    },
    data: {
      type: Object,
      required: true
    }
  },
  emits: ['close'],
  setup() {
    const {
      userDateFormat
    } = useDateFormat();
    return {
      userDateFormat
    };
  },
  data() {
    return {
      isLoading: false,
      education: {
        ...educationModel
      },
      rules: {
        institute: [shouldNotExceedCharLength(100)],
        major: [shouldNotExceedCharLength(100)],
        score: [shouldNotExceedCharLength(25)],
        year: [shouldNotExceedCharLength(4), digitsOnly],
        startDate: [validDateFormat(this.userDateFormat)],
        endDate: [validDateFormat(this.userDateFormat), endDateShouldBeAfterStartDate(() => this.education.startDate)]
      }
    };
  },
  beforeMount() {
    this.isLoading = true;
    this.http.get(this.data.id).then(response => {
      const {
        data
      } = response.data;
      this.education.name = data.education.name;
      this.education.institute = data.institute;
      this.education.major = data.major;
      this.education.year = data.year ? data.year : '';
      this.education.score = data.score;
      this.education.startDate = data.startDate ? data.startDate : '';
      this.education.endDate = data.endDate ? data.endDate : '';
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.update(this.data.id, {
        institute: this.education.institute,
        major: this.education.major,
        year: parseInt(this.education.year),
        score: this.education.score,
        startDate: this.education.startDate,
        endDate: this.education.endDate
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      this.$emit('close', true);
    }
  }
};
