import { APIService } from '@ohrm/core/util/services/api.service';
export default {
  name: 'CustomerAutocomplete',
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/time/customers');
    return {
      http
    };
  },
  methods: {
    async loadCustomers(serachParam) {
      return new Promise(resolve => {
        if (serachParam.trim()) {
          this.http.getAll({
            name: serachParam.trim(),
            status: 2
          }).then(({
            data
          }) => {
            resolve(data.data.map(customer => {
              return {
                id: customer.id,
                label: customer.name
              };
            }));
          });
        } else {
          resolve([]);
        }
      });
    }
  }
};
