import { APIService } from '@/core/util/services/api.service';
import { navigate } from '@ohrm/core/util/helper/navigation';
export default {
  name: 'ClaimActionButtons',
  props: {
    requestId: {
      type: Number,
      required: true
    },
    allowedActions: {
      type: Array,
      required: true
    },
    isAssigned: {
      default: false,
      type: Boolean,
      required: false
    }
  },
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, `/api/v2/claim/requests/${props.requestId}/action`);
    return {
      http
    };
  },
  computed: {
    isCancelAllowed() {
      return this.allowedActions.includes('Cancel');
    },
    isSubmitAllowed() {
      return this.allowedActions.includes('Submit');
    },
    isApproveAllowed() {
      return this.allowedActions.includes('Approve');
    },
    isRejectAllowed() {
      return this.allowedActions.includes('Reject');
    },
    isPayAllowed() {
      return this.allowedActions.includes('Pay');
    }
  },
  methods: {
    onClaimAction(action) {
      this.http.request({
        method: 'PUT',
        data: {
          action: action
        }
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.isAssigned ? navigate(`/claim/assignClaim/id/${this.requestId}`) : navigate(`/claim/submitClaim/id/${this.requestId}`);
      });
    },
    onBack() {
      this.isAssigned ? navigate('/claim/viewAssignClaim') : navigate('/claim/viewClaim');
    }
  }
};
