import "core-js/modules/es.array.push.js";
import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@ohrm/core/util/services/api.service';
import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
const skillModel = {
  id: '',
  name: '',
  description: ''
};
export default {
  props: {
    qualificationSkillId: {
      type: Number,
      required: true
    }
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/skills');
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      skill: {
        ...skillModel
      },
      rules: {
        name: [required, shouldNotExceedCharLength(120)],
        description: [shouldNotExceedCharLength(400)]
      }
    };
  },
  created() {
    this.isLoading = true;
    this.http.get(this.qualificationSkillId).then(response => {
      const {
        data
      } = response.data;
      this.skill.id = data.id;
      this.skill.name = data.name;
      this.skill.description = data.description;

      // Fetch list data for unique test
      return this.http.getAll({
        limit: 0
      });
    }).then(response => {
      const {
        data
      } = response.data;
      this.rules.name.push(v => {
        const index = data.findIndex(item => String(item.name).toLowerCase() == String(v).toLowerCase());
        if (index > -1) {
          const {
            id
          } = data[index];
          return id != this.qualificationSkillId ? this.$t('general.already_exists') : true;
        } else {
          return true;
        }
      });
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.update(this.qualificationSkillId, {
        name: this.skill.name,
        description: this.skill.description
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      navigate('/admin/viewSkills');
    }
  }
};
