import "core-js/modules/es.array.push.js";
import { computed, ref } from 'vue';
import { validSelection } from '@/core/util/validation/rules';
import { APIService } from '@/core/util/services/api.service';
import useSort from '@ohrm/core/util/composable/useSort';
import { navigate } from '@ohrm/core/util/helper/navigation';
import usePaginate from '@ohrm/core/util/composable/usePaginate';
import DeleteConfirmationDialog from '@ohrm/components/dialogs/DeleteConfirmationDialog';
import ReportAutocomplete from '@/orangehrmPimPlugin/components/ReportAutocomplete';
const defaultFilters = {
  report: null
};
const defaultSortOrder = {
  'report.name': 'ASC'
};
export default {
  components: {
    'delete-confirmation': DeleteConfirmationDialog,
    'report-autocomplete': ReportAutocomplete
  },
  setup() {
    const {
      sortDefinition,
      sortField,
      sortOrder,
      onSort
    } = useSort({
      sortDefinition: defaultSortOrder
    });
    const filters = ref({
      ...defaultFilters
    });
    const serializedFilters = computed(() => {
      return {
        reportId: filters.value.report?.id,
        sortField: sortField.value,
        sortOrder: sortOrder.value
      };
    });
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/pim/reports/defined');
    const {
      showPaginator,
      currentPage,
      total,
      pages,
      pageSize,
      response,
      isLoading,
      execQuery
    } = usePaginate(http, {
      query: serializedFilters
    });
    onSort(execQuery);
    return {
      http,
      showPaginator,
      currentPage,
      isLoading,
      total,
      pages,
      pageSize,
      execQuery,
      items: response,
      filters,
      sortDefinition
    };
  },
  data() {
    return {
      headers: [{
        name: 'name',
        slot: 'title',
        title: this.$t('general.name'),
        style: {
          flex: '85%'
        },
        sortField: 'report.name'
      }, {
        name: 'actions',
        title: this.$t('general.actions'),
        slot: 'action',
        style: {
          flex: '15%'
        },
        cellType: 'oxd-table-cell-actions',
        cellConfig: {
          delete: {
            onClick: this.onClickDelete,
            component: 'oxd-icon-button',
            props: {
              name: 'trash'
            }
          },
          edit: {
            onClick: this.onClickEdit,
            props: {
              name: 'pencil-fill'
            }
          },
          view: {
            onClick: this.onClickView,
            props: {
              name: 'file-text-fill'
            }
          }
        }
      }],
      checkedItems: [],
      rules: {
        report: [validSelection]
      }
    };
  },
  methods: {
    onClickAdd() {
      navigate('/pim/definePredefinedReport');
    },
    onClickEdit(item) {
      navigate('/pim/definePredefinedReport/{id}', {
        id: item.id
      });
    },
    onClickView(item) {
      navigate('/pim/displayPredefinedReport/{id}', {
        id: item.id
      });
    },
    onClickDeleteSelected() {
      const ids = [];
      this.checkedItems.forEach(index => {
        ids.push(this.items?.data[index].id);
      });
      this.$refs.deleteDialog.showDialog().then(confirmation => {
        if (confirmation === 'ok') {
          this.deleteItems(ids);
        }
      });
    },
    onClickDelete(item) {
      this.$refs.deleteDialog.showDialog().then(confirmation => {
        if (confirmation === 'ok') {
          this.deleteItems([item.id]);
        }
      });
    },
    deleteItems(items) {
      if (items instanceof Array) {
        this.isLoading = true;
        this.http.deleteAll({
          ids: items
        }).then(() => {
          return this.$toast.deleteSuccess();
        }).then(() => {
          this.isLoading = false;
          this.resetDataTable();
        });
      }
    },
    async resetDataTable() {
      this.checkedItems = [];
      await this.execQuery();
    },
    async filterItems() {
      await this.execQuery();
    },
    onClickReset() {
      this.filters = {
        ...defaultFilters
      };
      this.filterItems();
    }
  }
};
