import usePaginate from '@ohrm/core/util/composable/usePaginate';
import DeleteConfirmationDialog from '@ohrm/components/dialogs/DeleteConfirmationDialog';
import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@ohrm/core/util/services/api.service';
export default {
  components: {
    'delete-confirmation': DeleteConfirmationDialog
  },
  props: {
    customFieldLimit: {
      type: Number,
      required: true
    },
    screenList: {
      type: Array,
      required: true
    },
    fieldTypeList: {
      type: Array,
      required: true
    },
    unselectableIds: {
      type: Array,
      default: () => []
    }
  },
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/pim/custom-fields');
    const dataNormalizer = data => {
      return data.map(item => {
        const selectable = props.unselectableIds.findIndex(id => id == item.id);
        return {
          id: item.id,
          fieldName: item.fieldName,
          screen: props.screenList.filter(screen => {
            return item.screen === screen.id;
          })[0].label,
          fieldType: props.fieldTypeList.filter(fieldType => {
            return item.fieldType === fieldType.id;
          })[0].label,
          extraData: item.extraData,
          isSelectable: selectable === -1
        };
      });
    };
    const {
      showPaginator,
      currentPage,
      total,
      pages,
      pageSize,
      response,
      isLoading,
      execQuery
    } = usePaginate(http, {
      normalizer: dataNormalizer
    });
    return {
      http,
      showPaginator,
      currentPage,
      isLoading,
      total: total,
      pages,
      pageSize,
      execQuery,
      items: response
    };
  },
  data() {
    return {
      screenWidth: screen.width,
      headers: [{
        name: 'fieldName',
        slot: 'title',
        title: this.$t('pim.custom_field_name'),
        style: {
          flex: 2
        }
      }, {
        name: 'screen',
        title: this.$t('pim.screen'),
        style: {
          flex: 2
        }
      }, {
        name: 'fieldType',
        title: this.$t('pim.field_type'),
        style: {
          flex: 2
        }
      }, {
        name: 'actions',
        title: this.$t('general.actions'),
        slot: 'action',
        style: {
          flex: 1
        },
        cellType: 'oxd-table-cell-actions',
        cellConfig: {
          delete: {
            onClick: this.onClickDelete,
            component: 'oxd-icon-button',
            props: {
              name: 'trash'
            }
          },
          edit: {
            onClick: this.onClickEdit,
            props: {
              name: 'pencil-fill'
            }
          }
        }
      }],
      checkedItems: []
    };
  },
  computed: {
    isLoaded() {
      return !this.isLoading;
    },
    remainingFields() {
      return this.customFieldLimit - this.items?.data?.length;
    }
  },
  methods: {
    onClickAdd() {
      navigate('/pim/saveCustomFields');
    },
    onClickEdit(item) {
      navigate('/pim/saveCustomFields/{id}', {
        id: item.id
      });
    },
    onClickDeleteSelected() {
      const ids = this.checkedItems.map(index => {
        return this.items?.data[index].id;
      });
      this.$refs.deleteDialog.showDialog().then(confirmation => {
        if (confirmation === 'ok') {
          this.deleteItems(ids);
        }
      });
    },
    onClickDelete(item) {
      const isSelectable = this.unselectableIds.findIndex(id => id == item.id);
      if (isSelectable > -1) {
        return this.$toast.error({
          title: this.$t('general.error'),
          message: this.$t('pim.custom_fields_in_use')
        });
      }
      this.$refs.deleteDialog.showDialog().then(confirmation => {
        if (confirmation === 'ok') {
          this.deleteItems([item.id]);
        }
      });
    },
    deleteItems(items) {
      if (items instanceof Array) {
        this.isLoading = true;
        this.http.deleteAll({
          ids: items
        }).then(() => {
          return this.$toast.deleteSuccess();
        }).then(() => {
          this.isLoading = false;
          this.resetDataTable();
        });
      }
    },
    async resetDataTable() {
      this.checkedItems = [];
      await this.execQuery();
    }
  }
};
