import usePaginate from '@ohrm/core/util/composable/usePaginate';
import DeleteConfirmationDialog from '@ohrm/components/dialogs/DeleteConfirmationDialog';
import { APIService } from '@ohrm/core/util/services/api.service';
import SaveSubscriber from './SaveSubscriber';
import EditSubscriber from './EditSubscriber';
export default {
  components: {
    'save-subscriber': SaveSubscriber,
    'edit-subscriber': EditSubscriber,
    'delete-confirmation': DeleteConfirmationDialog
  },
  props: {
    subscriptionId: {
      type: String,
      required: true
    }
  },
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, `/api/v2/admin/email-subscriptions/${props.subscriptionId}/subscribers`);
    const {
      showPaginator,
      currentPage,
      total,
      pages,
      pageSize,
      response,
      isLoading,
      execQuery
    } = usePaginate(http);
    return {
      http,
      showPaginator,
      currentPage,
      isLoading,
      total,
      pages,
      pageSize,
      execQuery,
      items: response
    };
  },
  data() {
    return {
      headers: [{
        name: 'name',
        slot: 'title',
        title: this.$t('general.name'),
        style: {
          flex: '30%'
        }
      }, {
        name: 'email',
        title: this.$t('general.email'),
        style: {
          flex: '55%'
        }
      }, {
        name: 'actions',
        title: this.$t('general.actions'),
        slot: 'action',
        style: {
          flex: '15%'
        },
        cellType: 'oxd-table-cell-actions',
        cellConfig: {
          delete: {
            onClick: this.onClickDelete,
            component: 'oxd-icon-button',
            props: {
              name: 'trash'
            }
          },
          edit: {
            onClick: this.onClickEdit,
            props: {
              name: 'pencil-fill'
            }
          }
        }
      }],
      checkedItems: [],
      showSaveModal: false,
      showEditModal: false,
      editModalState: null
    };
  },
  computed: {
    title() {
      return this.items.meta ? this.items.meta?.name : '';
    }
  },
  methods: {
    onClickAdd() {
      this.showSaveModal = true;
    },
    onClickEdit(item) {
      this.editModalState = {
        ...item,
        subscriptionId: this.subscriptionId
      };
      this.showEditModal = true;
    },
    onSaveModalClose() {
      this.showSaveModal = false;
      this.resetDataTable();
    },
    onEditModalClose() {
      this.editModalState = null;
      this.showEditModal = false;
      this.resetDataTable();
    },
    onClickDeleteSelected() {
      const ids = this.checkedItems.map(index => {
        return this.items?.data[index].id;
      });
      this.$refs.deleteDialog.showDialog().then(confirmation => {
        if (confirmation === 'ok') {
          this.deleteItems(ids);
        }
      });
    },
    onClickDelete(item) {
      this.$refs.deleteDialog.showDialog().then(confirmation => {
        if (confirmation === 'ok') {
          this.deleteItems([item.id]);
        }
      });
    },
    deleteItems(items) {
      if (items instanceof Array) {
        this.isLoading = true;
        this.http.deleteAll({
          ids: items
        }).then(() => {
          return this.$toast.deleteSuccess();
        }).then(() => {
          this.isLoading = false;
          this.resetDataTable();
        });
      }
    },
    async resetDataTable() {
      this.checkedItems = [];
      await this.execQuery();
    }
  }
};
