import { APIService } from '@/core/util/services/api.service';
import FlexTable from '@/orangehrmSystemCheckPlugin/components/FlexTable';
export default {
  name: 'SystemCheckScreen',
  components: {
    'flex-table': FlexTable
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, `/api/v2/core/system-check`);
    return {
      http
    };
  },
  data() {
    return {
      items: [],
      isLoading: false,
      isInterrupted: false,
      error: null
    };
  },
  beforeMount() {
    this.fetchData();
  },
  methods: {
    fetchData() {
      this.isLoading = true;
      this.http.getAll().then(response => {
        const {
          data,
          meta
        } = response.data;
        this.items = data;
        this.isInterrupted = meta.isInterrupted;
        this.error = meta.error;
      }).finally(() => {
        this.isLoading = false;
      });
    },
    reCheck() {
      this.fetchData();
    }
  }
};
