import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import { promiseDebounce, OxdAlert } from '@ohrm/oxd';
import { urlFor } from '@/core/util/helper/url';
import { APIService } from '@/core/util/services/api.service';
import usePasswordPolicy from '@/core/util/composable/usePasswordPolicy';
import PasswordStrengthIndicator from '@/core/components/labels/PasswordStrengthIndicator';
export default {
  name: 'ResetWeakPassword',
  components: {
    'password-strength-indicator': PasswordStrengthIndicator,
    'oxd-alert': OxdAlert
  },
  props: {
    username: {
      type: String,
      required: true
    },
    code: {
      type: String,
      required: true
    },
    token: {
      type: String,
      required: true
    },
    error: {
      type: Object,
      default: () => null
    },
    invalidCode: {
      type: Boolean,
      default: () => false
    }
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '');
    const {
      passwordStrength,
      validatePassword
    } = usePasswordPolicy(http);
    return {
      http,
      passwordStrength,
      validatePassword
    };
  },
  data() {
    return {
      user: {
        username: '',
        currentPassword: '',
        confirmPassword: ''
      },
      rules: {
        currentPassword: [required, shouldNotExceedCharLength(64)],
        newPassword: [required, shouldNotExceedCharLength(64), promiseDebounce(this.validatePassword, 500)],
        confirmPassword: [required, shouldNotExceedCharLength(64), v => !!v && v === this.user.newPassword || this.$t('general.passwords_do_not_match')]
      }
    };
  },
  computed: {
    submitUrl() {
      return urlFor('/auth/resetWeakPassword');
    }
  },
  methods: {
    onSubmit() {
      this.$refs.resetForm.$el.submit();
    }
  }
};
