import { APIService } from '@ohrm/core/util/services/api.service';
import { OxdDialog } from '@ohrm/oxd';
import { maxFileSize, shouldNotExceedCharLength, validFileTypes } from '@ohrm/core/util/validation/rules';
import { truncate } from '@ohrm/core/util/helper/truncate';
const attachmentModel = {
  attachment: null,
  description: ''
};
export default {
  name: 'SaveAttachment',
  components: {
    'oxd-dialog': OxdDialog
  },
  props: {
    data: {
      type: Object,
      required: true
    },
    requestId: {
      type: Number,
      required: true
    },
    allowedFileTypes: {
      type: Array,
      required: true
    },
    maxFileSize: {
      type: Number,
      required: true
    }
  },
  emits: ['close'],
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, `api/v2/claim/requests/${props.requestId}/attachments`);
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      currentFile: '',
      attachment: {
        ...attachmentModel
      },
      rules: {
        description: [shouldNotExceedCharLength(200)],
        attachment: [maxFileSize(this.maxFileSize), validFileTypes(this.allowedFileTypes)]
      }
    };
  },
  beforeMount() {
    this.isLoading = true;
    this.http.get(this.data.id).then(response => {
      const {
        data
      } = response.data;
      this.currentFile = truncate(data.attachment.fileName);
      this.attachment.description = data.attachment.description;
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.update(this.data.id, {
        ...this.attachment
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        this.attachment = {
          ...attachmentModel
        };
        this.onCancel();
      });
    },
    onCancel() {
      this.$emit('close', true);
    }
  }
};
