import "core-js/modules/es.array.push.js";
import { required, validSelection, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import { APIService } from '@/core/util/services/api.service';
import { navigate } from '@ohrm/core/util/helper/navigation';
import { promiseDebounce } from '@ohrm/oxd';
import useEmployeeNameTranslate from '@/core/util/composable/useEmployeeNameTranslate';
import Activities from '@/orangehrmTimePlugin/components/Activities.vue';
import Users from '@/orangehrmTimePlugin/components/Users.vue';
import AddCustomerModal from '@/orangehrmTimePlugin/components/AddCustomerModal.vue';
import CustomerAutocomplete from '@/orangehrmTimePlugin/components/CustomerAutocomplete.vue';
import ProjectAdminAutocomplete from '@/orangehrmTimePlugin/components/ProjectAdminAutocomplete.vue';
const defaultProjectModel = {
  name: null,
  customer: {
    id: null,
    label: null
  },
  description: null,
  projectAdminEmpNumbers: [],
  forall: false,
  active: false
};
export default {
  name: 'ProjectEdit',
  components: {
    activities: Activities,
    users: Users,
    'add-customer-modal': AddCustomerModal,
    'customer-autocomplete': CustomerAutocomplete,
    'project-admin-autocomplete': ProjectAdminAutocomplete
  },
  props: {
    projectId: {
      type: Number,
      required: true
    },
    unselectableIds: {
      type: Array,
      default: () => []
    }
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/time/projects');
    http.setIgnorePath('/api/v2/time/validation/project-name');
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      projectAdmins: [{
        value: null
      }],
      project: {
        ...defaultProjectModel
      },
      showCustomerModal: false,
      rules: {
        name: [required, shouldNotExceedCharLength(50)],
        description: [shouldNotExceedCharLength(255)],
        customer: [required, validSelection],
        projectAdmin: [validSelection, value => {
          return this.projectAdmins.filter(({
            value: admin
          }) => admin && admin.id === value?.id).length < 2 ? true : this.$t('general.already_exists');
        }]
      }
    };
  },
  beforeMount() {
    this.isLoading = true;
    const {
      $tEmpName
    } = useEmployeeNameTranslate();
    this.http.get(this.projectId, {
      model: 'detailed'
    }).then(response => {
      const {
        data
      } = response.data;
      this.project.name = data.name;
      this.project.description = data.description;
      this.project.forall = data.forall;
      this.project.active = data.active;
      this.project.customer = {
        id: data.customer.id,
        label: data.customer.name
      };
      if (Array.isArray(data.projectAdmins) && data.projectAdmins.length > 0) {
        this.projectAdmins = data.projectAdmins.map(projectAdmin => {
          return {
            value: {
              id: projectAdmin.empNumber,
              label: $tEmpName(projectAdmin, {
                includeMiddle: true,
                excludePastEmpTag: true
              }),
              isPastEmployee: projectAdmin.terminationId ? true : false
            }
          };
        });
      }
    }).finally(() => {
      this.rules.name.push(promiseDebounce(this.validateProjectName, 500));
      this.isLoading = false;
    });
  },
  methods: {
    onClickAddCustomer() {
      this.showCustomerModal = true;
    },
    onCustomerModalClose(data) {
      if (data !== undefined) {
        const {
          id,
          name
        } = data;
        this.project.customer = {
          id,
          label: name
        };
      }
      this.showCustomerModal = false;
    },
    onAddAnother() {
      if (this.projectAdmins.length < 5) {
        this.projectAdmins.push({
          value: null
        });
      }
    },
    onRemoveAdmin(index) {
      this.projectAdmins.splice(index, 1);
    },
    onCancel() {
      navigate('/time/viewProjects');
    },
    onSave() {
      this.isLoading = true;
      this.http.update(this.projectId, {
        name: this.project.name,
        description: this.project.description,
        forall: this.project.forall,
        active: this.project.active,
        customerId: this.project.customer.id,
        projectAdminsEmpNumbers: this.projectAdmins.map(({
          value
        }) => value && value.id).filter(Number)
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    validateProjectName(project) {
      return new Promise(resolve => {
        if (project) {
          this.http.request({
            method: 'GET',
            url: `/api/v2/time/validation/project-name`,
            params: {
              projectId: this.projectId,
              projectName: this.project.name.trim(),
              customerId: this.project.customer?.id
            }
          }).then(response => {
            const {
              data
            } = response.data;
            return data.valid === true ? resolve(true) : resolve(this.$t('general.already_exists'));
          });
        } else {
          resolve(true);
        }
      });
    }
  }
};
