import { validLangString } from '@/core/util/validation/rules';
export default {
  props: {
    langstrings: {
      type: Array,
      required: true
    }
  },
  emits: ['update:langstrings'],
  setup(props, context) {
    const onUpdateTranslation = (value, index) => {
      context.emit('update:langstrings', props.langstrings.map((item, _index) => {
        if (_index === index) {
          return {
            ...item,
            target: value,
            modified: true
          };
        }
        return item;
      }));
    };
    return {
      onUpdateTranslation,
      rules: {
        langString: [validLangString]
      }
    };
  }
};
