import { APIService } from '@ohrm/core/util/services/api.service';
export default {
  name: 'VacancyAutocomplete',
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/recruitment/vacancies');
    return {
      http
    };
  },
  methods: {
    async loadVacancies(searchParam) {
      return new Promise(resolve => {
        if (searchParam.trim()) {
          this.http.getAll({
            name: searchParam.trim()
          }).then(({
            data
          }) => {
            resolve(data.data.map(vacancy => {
              return {
                id: vacancy.id,
                label: vacancy.name
              };
            }));
          });
        } else {
          resolve([]);
        }
      });
    }
  }
};
