import "core-js/modules/es.array.push.js";
import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import { onBeforeMount, reactive, ref, toRefs, watch } from 'vue';
import { APIService } from '@/core/util/services/api.service';
import { OxdDialog } from '@ohrm/oxd';
import ProjectUserAutocomplete from '@/orangehrmTimePlugin/components/ProjectUserAutocomplete.vue';
// const filter={
