export default {
  name: 'SaveUserModal',
  components: {
    'oxd-dialog': OxdDialog,
    'projectUser-autocomplete': ProjectUserAutocomplete
  },
  props: {
    projectId: {
      type: Number,
      required: true
    },
    empArray: {
      type: Array,
      required: true
    },
    username: {
      type: String,
      required: true
    }
  },
  beforeCreate() {
    this.$watch(() => this.empArray, (newArray, oldArray) => {
      // Handle the array changes here
      console.log('Array changed:', newArray, oldArray);

      // You can perform any custom logic based on the changes
      // For example, trigger a function or update a computed property
    });
  },

  emits: ['close'],
  setup(props) {
    const filters = ref({
      userId: null
    });
    const http = new APIService(window.appGlobal.baseUrl, `/api/v2/time/project/${props.projectId}/activities`);
    console.loadEmployees;
    return {
      http,
      filters
    };
  },
  data() {
    return {
      isLoading: false,
      name: '',
      rules: {
        name: [required, shouldNotExceedCharLength(100)]
      }
    };
  },
  beforeMount() {
    this.isLoading = true;
    this.http.getAll({
      limit: 0
    }).then(response => {
      const {
        data
      } = response.data;
      if (data) {
        this.rules.name.push(v => {
          const index = data.findIndex(item => String(item.name).toLowerCase() == String(v).toLowerCase());
          if (index > -1) {
            return this.$t('general.already_exists');
          } else {
            return true;
          }
        });
      }
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.create({
        name: this.name
      }).then(() => {
        this.$toast.saveSuccess();
        this.onCancel();
      });
    },
    onCancel() {
      this.$emit('close', true);
    }
  }
};
