import { toRefs } from 'vue';
import { navigate } from '@/core/util/helper/navigation';
import { useResponsive } from '@ohrm/oxd';
export default {
  name: 'VacancyCard',
  props: {
    vacancyId: {
      type: Number,
      required: true
    },
    vacancyTitle: {
      type: String,
      required: true
    },
    vacancyDescription: {
      type: String,
      required: true
    }
  },
  setup() {
    const responsiveState = useResponsive();
    return {
      ...toRefs(responsiveState)
    };
  },
  data() {
    return {
      viewMore: false
    };
  },
  computed: {
    isMobile() {
      return this.windowWidth < 600;
    },
    isViewDetails() {
      return !this.viewMore;
    },
    descriptionLength() {
      if (this.isMobile) return 150;
      return this.windowWidth < 1920 ? 250 : 400;
    }
  },
  methods: {
    viewDetails() {
      this.viewMore = !this.viewMore;
    },
    apply() {
      navigate('/recruitmentApply/applyVacancy/id/{id}', {
        id: this.vacancyId
      });
    }
  }
};
