import useDateFormat from '@/core/util/composable/useDateFormat';
import { formatDate, parseDate } from '@/core/util/helper/datefns';
import useLocale from '@/core/util/composable/useLocale';
export default {
  name: 'LeaveConflict',
  props: {
    workshiftExceeded: {
      type: Boolean,
      default: false
    },
    data: {
      type: Array,
      required: true
    }
  },
  setup() {
    const {
      jsDateFormat
    } = useDateFormat();
    const {
      locale
    } = useLocale();
    return {
      locale,
      jsDateFormat
    };
  },
  data() {
    return {
      headers: [{
        name: 'date',
        title: this.$t('general.date'),
        style: {
          flex: 1
        }
      }, {
        name: 'hours',
        title: this.$t('leave.no_of_hours'),
        style: {
          flex: 1
        }
      }, {
        name: 'type',
        title: this.$t('leave.leave_type'),
        style: {
          flex: 1
        }
      }, {
        name: 'status',
        title: this.$t('general.status'),
        style: {
          flex: 1
        }
      }, {
        name: 'comments',
        title: this.$t('general.comments'),
        style: {
          flex: 1
        }
      }]
    };
  },
  computed: {
    header() {
      return this.workshiftExceeded ? this.$t('leave.workshift_length_exceeded_due_to_the_following_leave_request') : this.$t('leave.overlapping_leave_request_found');
    },
    items() {
      return this.data.map(item => {
        return {
          date: formatDate(parseDate(item.date), this.jsDateFormat, {
            locale: this.locale
          }),
          hours: parseFloat(item.lengthHours).toFixed(2),
          type: item.leaveType?.name,
          status: item.status?.name,
          comments: item.lastComment?.comment
        };
      });
    }
  }
};
