import { APIService } from '@/core/util/services/api.service';
import { OxdSwitchInput } from '@ohrm/oxd';
const configsModel = {
  canUserChangeCurrentTime: false,
  canUserModifyAttendance: false,
  canSupervisorModifyAttendance: false
};
export default {
  components: {
    'oxd-switch-input': OxdSwitchInput
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/attendance/configs');
    return {
      http
    };
  },
  data() {
    return {
      config: {
        ...configsModel
      },
      isLoading: false
    };
  },
  beforeMount() {
    this.isLoading = true;
    this.http.getAll().then(response => {
      const {
        data
      } = response.data;
      this.config = {
        ...data
      };
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.request({
        method: 'PUT',
        data: {
          ...this.config
        }
      }).then(response => {
        const {
          data
        } = response.data;
        this.config = {
          ...data
        };
        return this.$toast.saveSuccess();
      }).finally(() => {
        this.isLoading = false;
      });
    }
  }
};
