import { ref, onBeforeMount } from 'vue';
import { APIService } from '@ohrm/core/util/services/api.service';
export default {
  name: 'CustomerDropdown',
  setup() {
    const options = ref([]);
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/time/customers');
    onBeforeMount(() => {
      http.getAll({
        status: 1,
        limit: 0
      }).then(({
        data
      }) => {
        options.value = data.data.map(item => {
          return {
            id: item.id,
            label: item.name
          };
        });
      });
    });
    return {
      options
    };
  }
};
