import { navigate } from '@/core/util/helper/navigation';
import ReviewerAutoComplete from '@/orangehrmPerformancePlugin/components/ReviewerAutoComplete';
import EmployeeAutocomplete from '@/core/components/inputs/EmployeeAutocomplete';
import { APIService } from '@/core/util/services/api.service';
import { required, validSelection, shouldNotExceedCharLength } from '@/core/util/validation/rules';
const trackerModel = {
  name: null,
  employee: null,
  reviewers: []
};
export default {
  components: {
    'reviewer-autocomplete': ReviewerAutoComplete,
    'employee-autocomplete': EmployeeAutocomplete
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/performance/config/trackers');
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      tracker: {
        ...trackerModel
      },
      rules: {
        tracker: [required, shouldNotExceedCharLength(200)],
        employee: [required, validSelection, value => {
          if (value === null) {
            return true;
          }
          const valid = this.tracker.reviewers.findIndex(reviewer => {
            return reviewer.id === value.id;
          });
          if (valid == -1) {
            return true;
          }
          return this.$t('performance.employee_cannot_be_assigned_as_his_own_reviewer');
        }],
        reviewers: [required, validSelection]
      }
    };
  },
  methods: {
    onCancel() {
      navigate('/performance/viewPerformanceTracker');
    },
    onSave() {
      this.isLoading = true;
      this.http.create({
        trackerName: this.tracker.name.trim(),
        empNumber: this.tracker.employee.id,
        reviewerEmpNumbers: this.tracker.reviewers.map(employee => employee.id)
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        // go back
        this.onCancel();
      });
    }
  }
};
