import { computed } from 'vue';
import useDateFormat from '@/core/util/composable/useDateFormat';
import { formatDate, parseDate } from '@/core/util/helper/datefns';
import useLocale from '@/core/util/composable/useLocale';
const defaultPic = `${window.appGlobal.publicPath}/images/default-photo.png`;
export default {
  name: 'LeaveComment',
  props: {
    data: {
      type: Object,
      required: true
    }
  },
  setup(props) {
    const imgSrc = computed(() => {
      const employee = props.data.createdByEmployee;
      return employee ? `${window.appGlobal.baseUrl}/pim/viewPhoto/empNumber/${employee.empNumber}` : defaultPic;
    });
    const fullName = computed(() => {
      const employee = props.data.createdByEmployee;
      return employee && `${employee.firstName} ${employee.lastName}`;
    });
    const {
      jsDateFormat
    } = useDateFormat();
    const {
      locale
    } = useLocale();
    const commentDate = computed(() => {
      return formatDate(parseDate(props.data?.date), jsDateFormat, {
        locale
      });
    });
    return {
      imgSrc,
      fullName,
      commentDate
    };
  }
};
