import QualificationDropdown from '@/orangehrmPimPlugin/components/QualificationDropdown';
import { required, validDateFormat, shouldNotExceedCharLength, endDateShouldBeAfterStartDate } from '@ohrm/core/util/validation/rules';
import { yearRange } from '@ohrm/core/util/helper/year-range';
import useDateFormat from '@/core/util/composable/useDateFormat';
const licenseModel = {
  licenseId: null,
  licenseNo: '',
  issuedDate: '',
  expiryDate: ''
};
export default {
  name: 'SaveLicense',
  components: {
    'qualification-dropdown': QualificationDropdown
  },
  props: {
    http: {
      type: Object,
      required: true
    },
    api: {
      type: String,
      required: true
    }
  },
  emits: ['close'],
  setup() {
    const {
      userDateFormat
    } = useDateFormat();
    return {
      userDateFormat
    };
  },
  data() {
    return {
      isLoading: false,
      license: {
        ...licenseModel
      },
      yearArray: [...yearRange()],
      rules: {
        licenseId: [required],
        licenseNo: [shouldNotExceedCharLength(50)],
        issuedDate: [validDateFormat(this.userDateFormat)],
        expiryDate: [validDateFormat(this.userDateFormat), endDateShouldBeAfterStartDate(() => this.license.issuedDate, this.$t('pim.expiry_date_should_be_after_issued_date'))]
      }
    };
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.create({
        ...this.license,
        licenseId: this.license.licenseId?.id
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      this.$emit('close', true);
    }
  }
};
