import { required, maxFileSize, shouldNotExceedCharLength, validFileTypes } from '@ohrm/core/util/validation/rules';
const attachmentModel = {
  attachment: null,
  description: ''
};
export default {
  name: 'SaveAttachment',
  props: {
    http: {
      type: Object,
      required: true
    },
    allowedFileTypes: {
      type: Array,
      required: true
    },
    maxFileSize: {
      type: Number,
      required: true
    }
  },
  emits: ['close'],
  data() {
    return {
      isLoading: false,
      attachment: {
        ...attachmentModel
      },
      rules: {
        description: [shouldNotExceedCharLength(200)],
        attachment: [required, maxFileSize(this.maxFileSize), validFileTypes(this.allowedFileTypes)]
      }
    };
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.create({
        ...this.attachment
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.attachment = {
          ...attachmentModel
        };
        this.onCancel();
      });
    },
    onCancel() {
      this.$emit('close', true);
    }
  }
};
