import { OxdDialog } from '@ohrm/oxd';
export default {
  name: 'SuccessDialog',
  components: {
    'simple-dialog': OxdDialog
  },
  data() {
    return {
      show: false,
      resolve: null
    };
  },
  methods: {
    showSuccessDialog() {
      return new Promise(resolve => {
        this.resolve = resolve;
        this.show = true;
      });
    },
    onSuccess() {
      this.show = false;
      this.resolve && this.resolve('ok');
    }
  }
};
