import { APIService } from '@ohrm/core/util/services/api.service';
import { reloadPage } from '@ohrm/core/util/helper/navigation';
export default {
  props: {
    dateFormatList: {
      type: Array,
      required: true
    },
    languageList: {
      type: Array,
      required: true
    }
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/localization');
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      configuration: {
        language: null,
        dateFormat: null
      }
    };
  },
  beforeMount() {
    this.isLoading = true;
    this.http.getAll().then(response => {
      const {
        data
      } = response.data;
      this.configuration.language = this.languageList.find(item => item.id === data.language);
      this.configuration.dateFormat = this.dateFormatList.find(item => item.id === data.dateFormat);
    }).finally(() => this.isLoading = false);
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.request({
        method: 'PUT',
        url: '/api/v2/admin/localization',
        data: {
          language: this.configuration.language?.id,
          dateFormat: this.configuration.dateFormat?.id
        }
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => reloadPage());
    }
  }
};
