import { APIService } from '@ohrm/core/util/services/api.service';
export default {
  name: 'CandidateAutocomplete',
  props: {
    params: {
      type: Object,
      default: () => ({})
    }
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/recruitment/candidates');
    return {
      http
    };
  },
  methods: {
    async loadCandidates(serachParam) {
      return new Promise(resolve => {
        if (serachParam.trim()) {
          this.http.getAll({
            candidateName: serachParam.trim(),
            ...this.params
          }).then(({
            data
          }) => {
            resolve(data.data.map(candidate => {
              return {
                id: candidate.id,
                label: `${candidate.firstName} ${candidate.middleName || ''} ${candidate.lastName}`,
                _candidate: candidate
              };
            }));
          });
        } else {
          resolve([]);
        }
      });
    }
  }
};
