import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@ohrm/core/util/services/api.service';
import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import { promiseDebounce } from '@ohrm/oxd';
const customerModel = {
  id: '',
  name: '',
  description: ''
};
export default {
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/time/customers');
    http.setIgnorePath('/api/v2/time/validation/customer-name');
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      customer: {
        ...customerModel
      },
      rules: {
        name: [required, shouldNotExceedCharLength(50), promiseDebounce(this.validateCustomerName, 500)],
        description: [shouldNotExceedCharLength(255)]
      }
    };
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.create({
        name: this.customer.name,
        description: this.customer.description
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      navigate('/time/viewCustomers');
    },
    validateCustomerName(customer) {
      return new Promise(resolve => {
        if (customer) {
          this.http.request({
            method: 'GET',
            url: `/api/v2/time/validation/customer-name`,
            params: {
              customerName: this.customer.name.trim()
            }
          }).then(response => {
            const {
              data
            } = response.data;
            return data.valid === true ? resolve(true) : resolve(this.$t('general.already_exists'));
          });
        } else {
          resolve(true);
        }
      });
    }
  }
};
