import { APIService } from '@/core/util/services/api.service';
import usePaginate from '@ohrm/core/util/composable/usePaginate';
import { convertFilesizeToString } from '@ohrm/core/util/helper/filesize';
import DeleteConfirmationDialog from '@ohrm/components/dialogs/DeleteConfirmationDialog.vue';
import SaveInterviewAttachment from '@/orangehrmRecruitmentPlugin/components/SaveInterviewAttachment.vue';
import EditInterviewAttachment from '@/orangehrmRecruitmentPlugin/components/EditInterviewAttachment.vue';
const attachmentDataNormalizer = data => {
  return data.map(item => {
    return {
      id: item.id,
      interviewId: item.interviewId,
      filename: item.attachment?.fileName,
      size: convertFilesizeToString(item.attachment?.fileSize || 0, 2),
      fileType: item.attachment?.fileType,
      comment: item.comment
    };
  });
};
export default {
  components: {
    'delete-confirmation': DeleteConfirmationDialog,
    'save-interview-attachment': SaveInterviewAttachment,
    'edit-interview-attachment': EditInterviewAttachment
  },
  props: {
    interviewId: {
      type: Number,
      required: true
    },
    maxFileSize: {
      type: Number,
      required: true
    },
    allowedFileTypes: {
      type: Array,
      required: true
    }
  },
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, `/api/v2/recruitment/interviews/${props.interviewId}/attachments`);
    const {
      showPaginator,
      currentPage,
      total,
      pages,
      pageSize,
      response,
      isLoading,
      execQuery
    } = usePaginate(http, {
      normalizer: attachmentDataNormalizer,
      toastNoRecords: false
    });
    return {
      http,
      showPaginator,
      currentPage,
      isLoading,
      total,
      pages,
      pageSize,
      execQuery,
      items: response
    };
  },
  data() {
    return {
      headers: [{
        name: 'filename',
        slot: 'title',
        title: this.$t('general.file_name'),
        style: {
          flex: 1
        }
      }, {
        name: 'size',
        title: this.$t('general.size'),
        style: {
          flex: 1
        }
      }, {
        name: 'fileType',
        title: this.$t('general.type'),
        style: {
          flex: 1
        }
      }, {
        name: 'comment',
        title: this.$t('general.comment'),
        style: {
          flex: 1
        }
      }, {
        name: 'actions',
        slot: 'action',
        title: this.$t('general.actions'),
        style: {
          flex: 1
        },
        cellType: 'oxd-table-cell-actions',
        cellConfig: {
          edit: {
            onClick: this.onClickEdit,
            props: {
              name: 'pencil-fill'
            }
          },
          delete: {
            onClick: this.onClickDelete,
            component: 'oxd-icon-button',
            props: {
              name: 'trash'
            }
          },
          download: {
            onClick: this.onClickDownload,
            props: {
              name: 'download'
            }
          }
        }
      }],
      checkedItems: [],
      showSaveModal: false,
      showEditModal: false,
      editModalState: null
    };
  },
  methods: {
    onClickDeleteSelected() {
      const ids = this.checkedItems.map(index => {
        return this.items?.data[index].id;
      });
      this.$refs.deleteDialog.showDialog().then(confirmation => {
        if (confirmation === 'ok') {
          this.deleteItems(ids);
        }
      });
    },
    onClickDelete(item) {
      this.$refs.deleteDialog.showDialog().then(confirmation => {
        if (confirmation === 'ok') {
          this.deleteItems([item.id]);
        }
      });
    },
    deleteItems(items) {
      if (items instanceof Array) {
        this.isLoading = true;
        this.http.deleteAll({
          ids: items
        }).then(() => {
          return this.$toast.deleteSuccess();
        }).then(() => {
          this.isLoading = false;
          this.resetDataTable();
        });
      }
    },
    async resetDataTable() {
      this.checkedItems = [];
      await this.execQuery();
    },
    onClickAdd() {
      this.showSaveModal = true;
      this.showEditModal = false;
    },
    onClickEdit(item) {
      this.showSaveModal = false;
      this.showEditModal = true;
      this.editModalState = item;
    },
    closeModel() {
      this.showSaveModal = false;
      this.showEditModal = false;
      this.resetDataTable();
    },
    onClickDownload(item) {
      const downUrl = `${window.appGlobal.baseUrl}/recruitment/viewInterviewAttachment/interview/${this.interviewId}/attachment/${item.id}`;
      window.open(downUrl, '_blank');
    }
  }
};
