import ProfileImage from '@/orangehrmBuzzPlugin/components/ProfileImage';
import { OxdDialog } from '@ohrm/oxd';
export default {
  name: 'PostModal',
  components: {
    'oxd-dialog': OxdDialog,
    'profile-image': ProfileImage
  },
  props: {
    title: {
      type: String,
      required: true
    },
    employee: {
      type: Object,
      required: true
    },
    loading: {
      type: Boolean,
      default: false
    },
    disabled: {
      type: Boolean,
      default: false
    },
    actionLabel: {
      type: String,
      default: null,
      required: false
    }
  },
  emits: ['close', 'submit'],
  setup(_, context) {
    const onSubmit = () => {
      context.emit('submit');
    };
    const onCancel = () => {
      context.emit('close');
    };
    return {
      onSubmit,
      onCancel
    };
  }
};
