import { APIService } from '@ohrm/core/util/services/api.service';
export default {
  name: 'ReferenceIdAutocomplete',
  props: {
    isAssigned: {
      default: false,
      type: Boolean,
      required: false
    }
  },
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, props.isAssigned ? '/api/v2/claim/employees/requests?model=summary' : '/api/v2/claim/requests?model=summary');
    return {
      http
    };
  },
  methods: {
    async loadTypes(serachParam) {
      return new Promise(resolve => {
        if (serachParam.trim()) {
          const params = {
            referenceId: serachParam.trim()
          };
          this.http.getAll(params).then(({
            data
          }) => {
            resolve(data.data.map(claimRequest => {
              return {
                id: claimRequest.referenceId,
                label: claimRequest.referenceId
              };
            }));
          });
        } else {
          resolve([]);
        }
      });
    }
  }
};
