import { APIService } from '@ohrm/core/util/services/api.service';
import LanguagesDropdown from '@/orangehrmAdminPlugin/components/LanguagesDropdown.vue';
import { required } from '@/core/util/validation/rules';
import { OxdDialog } from '@ohrm/oxd';
export default {
  name: 'AddLanguageModal',
  components: {
    'oxd-dialog': OxdDialog,
    'languages-dropdown': LanguagesDropdown
  },
  emits: ['close'],
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/i18n/languages');
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      language: null,
      rules: {
        language: [required]
      }
    };
  },
  methods: {
    onSave() {
      this.http.update(this.language.id, null).then(response => {
        if (response) {
          return this.$toast.saveSuccess();
        }
      }).then(() => {
        this.isLoading = false;
        this.$emit('close');
      });
    },
    onCancel() {
      this.$emit('close');
    }
  }
};
