import { OxdDialog } from '@ohrm/oxd';
export default {
  components: {
    'oxd-dialog': OxdDialog
  },
  props: {
    title: {
      type: String,
      required: true
    },
    subtitle: {
      type: String,
      required: true
    },
    cancelLabel: {
      type: String,
      required: true
    },
    confirmLabel: {
      type: String,
      required: true
    },
    icon: {
      type: String,
      required: false,
      default: ''
    },
    confirmButtonType: {
      type: String,
      required: false,
      default: 'label-danger'
    },
    cancelButtonType: {
      type: String,
      required: false,
      default: 'text'
    }
  },
  data() {
    return {
      show: false,
      reject: null,
      resolve: null
    };
  },
  methods: {
    showDialog() {
      return new Promise((resolve, reject) => {
        this.resolve = resolve;
        this.reject = reject;
        this.show = true;
      });
    },
    onConfirm() {
      this.show = false;
      this.resolve && this.resolve('ok');
    },
    onCancel() {
      this.show = false;
      this.resolve && this.resolve('cancel');
    }
  }
};
