import "core-js/modules/es.array.push.js";
import { navigate, reloadPage } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@ohrm/core/util/services/api.service';
import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
import PayGradeCurrency from '@/orangehrmAdminPlugin/pages/payGrade/PayGradeCurrency.vue';
export default {
  components: {
    'pay-grade-currency': PayGradeCurrency
  },
  props: {
    payGradeId: {
      type: String,
      required: true
    }
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/pay-grades');
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      grade: {
        id: '',
        name: ''
      },
      rules: {
        name: [required, shouldNotExceedCharLength(50)]
      },
      errors: []
    };
  },
  created() {
    this.isLoading = true;
    this.http.get(this.payGradeId).then(response => {
      const {
        data
      } = response.data;
      this.grade.id = data.id;
      this.grade.name = data.name;
      return this.http.getAll();
    }).then(response => {
      const {
        data
      } = response.data;
      this.rules.name.push(v => {
        const index = data.findIndex(item => String(item.name).toLowerCase() == String(v).toLowerCase());
        if (index > -1) {
          const {
            id
          } = data[index];
          return id != this.grade.id ? this.$t('general.already_exists') : true;
        } else {
          return true;
        }
      });
      this.isLoading = false;
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.update(this.payGradeId, {
        name: this.grade.name
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        reloadPage();
      });
    },
    onCancel() {
      navigate('/admin/viewPayGrades');
    }
  }
};
