import "core-js/modules/es.array.push.js";
import usePaginate from '@ohrm/core/util/composable/usePaginate';
import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@/core/util/services/api.service';
import DeleteConfirmationDialog from '@ohrm/components/dialogs/DeleteConfirmationDialog.vue';
import { computed, ref } from 'vue';
import useSort from '@ohrm/core/util/composable/useSort';
const defaultFilters = {
  name: '',
  city: '',
  countryCode: {}
};
const defaultSortOrder = {
  'location.name': 'ASC',
  'location.city': 'DEFAULT',
  'country.countryName': 'DEFAULT',
  'location.phone': 'DEFAULT',
  noOfEmployees: 'DEFAULT'
};
const locationDataNormalizer = data => {
  return data.map(location => {
    return {
      id: location.id,
      name: location.name,
      city: location.city,
      country: location.country.countryName,
      phone: location.phone,
      noOfEmployees: location.noOfEmployees ? location.noOfEmployees : 0
    };
  });
};
export default {
  components: {
    'delete-confirmation': DeleteConfirmationDialog
  },
  props: {
    countries: {
      type: Array,
      default: () => []
    }
  },
  setup() {
    const {
      sortDefinition,
      sortField,
      sortOrder,
      onSort
    } = useSort({
      sortDefinition: defaultSortOrder
    });
    const filters = ref({
      ...defaultFilters
    });
    const serializedFilters = computed(() => {
      return {
        name: filters.value.name,
        city: filters.value.city,
        countryCode: filters.value.countryCode?.id,
        sortField: sortField.value,
        sortOrder: sortOrder.value
      };
    });
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/admin/locations');
    const {
      showPaginator,
      currentPage,
      total,
      pages,
      pageSize,
      response,
      isLoading,
      execQuery
    } = usePaginate(http, {
      query: serializedFilters,
      normalizer: locationDataNormalizer
    });
    onSort(execQuery);
    return {
      http,
      showPaginator,
      currentPage,
      isLoading,
      total,
      pages,
      pageSize,
      execQuery,
      items: response,
      filters,
      sortDefinition
    };
  },
  data() {
    const cellConfig = {
      delete: {
        onClick: this.onClickDelete,
        component: 'oxd-icon-button',
        props: {
          name: 'trash'
        }
      },
      edit: {
        onClick: this.onClickEdit,
        props: {
          name: 'pencil-fill'
        }
      }
    };
    return {
      headers: [{
        name: 'name',
        slot: 'title',
        title: this.$t('general.name'),
        style: {
          flex: 1
        },
        sortField: 'location.name'
      }, {
        name: 'city',
        title: this.$t('general.city'),
        style: {
          flex: 1
        },
        sortField: 'location.city'
      }, {
        name: 'country',
        title: this.$t('general.country'),
        style: {
          flex: 1
        },
        sortField: 'country.countryName'
      }, {
        name: 'phone',
        title: this.$t('general.phone'),
        style: {
          flex: 1
        },
        sortField: 'location.phone'
      }, {
        name: 'noOfEmployees',
        title: this.$t('admin.number_of_employees'),
        style: {
          flex: 1
        },
        sortField: 'noOfEmployees'
      }, {
        name: 'actions',
        title: this.$t('general.actions'),
        slot: 'action',
        style: {
          flex: 1
        },
        cellType: 'oxd-table-cell-actions',
        cellConfig: cellConfig
      }],
      checkedItems: []
    };
  },
  methods: {
    onClickAdd() {
      navigate('/admin/saveLocation');
    },
    onClickEdit(item) {
      navigate('/admin/saveLocation/{id}', {
        id: item.id
      });
    },
    onClickDeleteSelected() {
      const ids = [];
      this.checkedItems.forEach(index => {
        ids.push(this.items?.data[index].id);
      });
      this.$refs.deleteDialog.showDialog().then(confirmation => {
        if (confirmation === 'ok') {
          this.deleteItems(ids);
        }
      });
    },
    onClickDelete(item) {
      this.$refs.deleteDialog.showDialog().then(confirmation => {
        if (confirmation === 'ok') {
          this.deleteItems([item.id]);
        }
      });
    },
    deleteItems(items) {
      if (items instanceof Array) {
        this.isLoading = true;
        this.http.deleteAll({
          ids: items
        }).then(() => {
          return this.$toast.deleteSuccess();
        }).then(() => {
          this.isLoading = false;
          this.resetDataTable();
        });
      }
    },
    async resetDataTable() {
      this.checkedItems = [];
      await this.execQuery();
    },
    async filterItems() {
      await this.execQuery();
    },
    onClickReset() {
      this.filters = {
        ...defaultFilters
      };
      this.filterItems();
    }
  }
};
