import { maxFileSize, shouldNotExceedCharLength, validFileTypes } from '@ohrm/core/util/validation/rules';
const attachmentModel = {
  attachment: null,
  description: ''
};
export default {
  name: 'EditAttachment',
  props: {
    http: {
      type: Object,
      required: true
    },
    data: {
      type: Object,
      required: true
    },
    allowedFileTypes: {
      type: Array,
      required: true
    },
    maxFileSize: {
      type: Number,
      required: true
    }
  },
  emits: ['close'],
  data() {
    return {
      isLoading: false,
      currentFile: '',
      attachment: {
        ...attachmentModel
      },
      rules: {
        description: [shouldNotExceedCharLength(200)],
        attachment: [maxFileSize(this.maxFileSize), validFileTypes(this.allowedFileTypes)]
      }
    };
  },
  beforeMount() {
    this.isLoading = true;
    this.http.get(this.data.id).then(response => {
      const {
        data
      } = response.data;
      this.currentFile = data.filename;
      this.attachment.description = data.description;
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.update(this.data.id, {
        ...this.attachment
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        this.attachment = {
          ...attachmentModel
        };
        this.onCancel();
      });
    },
    onCancel() {
      this.$emit('close', true);
    }
  }
};
