import { OxdIcon } from '@ohrm/oxd';
export default {
  name: 'FileUploadInput',
  components: {
    'oxd-icon': OxdIcon
  },
  inheritAttrs: false,
  props: {
    label: {
      type: String,
      default: ''
    },
    hint: {
      type: String,
      default: ''
    },
    url: {
      type: [String, Function],
      required: true
    },
    method: {
      type: String,
      required: true
    },
    file: {
      type: Object,
      required: false,
      default: () => null
    },
    newFile: {
      type: Object,
      required: false,
      default: () => null
    },
    disabled: {
      type: Boolean,
      default: false
    },
    deletable: {
      type: Boolean,
      default: true
    }
  },
  emits: ['update:method', 'update:newFile'],
  computed: {
    fileSelected() {
      return this.file && Object.keys(this.file).length > 0;
    },
    fieldLabel() {
      return this.method === 'replaceCurrent' && this.fileSelected ? this.$t('general.upload_n_file', {
        fileName: this.label
      }) : this.label;
    }
  },
  methods: {
    downloadFile() {
      let downUrl;
      if (typeof this.url === 'function') {
        downUrl = this.url(this.$props);
      } else {
        if (!this.file?.id) return;
        downUrl = `${window.appGlobal.baseUrl}/${this.url}/${this.file.id}`;
      }
      window.open(downUrl, '_blank');
    }
  }
};
