import { OxdIcon, OxdTableDropdown } from '@ohrm/oxd';
export default {
  name: 'EmployeeTrackerLogCard',
  components: {
    'oxd-icon': OxdIcon,
    'oxd-table-dropdown': OxdTableDropdown
  },
  props: {
    trackerLog: {
      type: Object,
      required: true
    }
  },
  emits: ['edit', 'delete'],
  data() {
    return {
      dropdownOptions: [{
        label: this.$t('general.edit'),
        context: 'edit'
      }, {
        label: this.$t('performance.delete'),
        context: 'delete'
      }]
    };
  },
  methods: {
    onTrackerDropdownAction(event, item) {
      switch (event.context) {
        case 'edit':
          this.$emit('edit', item.id);
          break;
        case 'delete':
          this.$emit('delete', item.id);
          break;
      }
    }
  }
};
