import "core-js/modules/es.array.push.js";
import { navigate } from '@ohrm/core/util/helper/navigation';
import { APIService } from '@ohrm/core/util/services/api.service';
import { required, shouldNotExceedCharLength } from '@ohrm/core/util/validation/rules';
const customFieldModel = {
  fieldName: '',
  screen: '',
  fieldType: '',
  extraData: ''
};
export default {
  props: {
    screenList: {
      type: Array,
      required: true
    },
    fieldTypeList: {
      type: Array,
      required: true
    }
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/pim/custom-fields');
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      customField: {
        ...customFieldModel
      },
      rules: {
        fieldName: [required, shouldNotExceedCharLength(250)],
        screen: [required, shouldNotExceedCharLength(100)],
        fieldType: [required, shouldNotExceedCharLength(15)],
        extraData: [required, shouldNotExceedCharLength(250)]
      }
    };
  },
  computed: {
    isDropDownField() {
      return this.customField.fieldType?.id === 1;
    }
  },
  created() {
    this.isLoading = true;
    this.http.getAll().then(response => {
      const {
        data
      } = response.data;
      this.rules.fieldName.push(v => {
        const index = data.findIndex(item => item.fieldName == v);
        return index === -1 || this.$t('general.already_exists');
      });
    }).finally(() => {
      this.isLoading = false;
    });
  },
  methods: {
    onSave() {
      this.isLoading = true;
      const fieldType = this.customField.fieldType.id;
      this.http.create({
        fieldName: this.customField.fieldName,
        screen: this.customField.screen.id,
        fieldType: fieldType,
        extraData: fieldType === 1 ? this.customField.extraData : null
      }).then(() => {
        return this.$toast.saveSuccess();
      }).then(() => {
        this.onCancel();
      });
    },
    onCancel() {
      navigate('/pim/listCustomFields');
    }
  }
};
