import { urlFor } from '@/core/util/helper/url';
import LoginBranding from '@/orangehrmAuthenticationPlugin/components/LoginBranding.vue';
import { OxdAlert } from '@ohrm/oxd';
export default {
  name: 'OAuthAuthorize',
  components: {
    'login-branding': LoginBranding,
    'oxd-alert': OxdAlert
  },
  props: {
    params: {
      type: Object,
      required: true
    },
    clientName: {
      type: String,
      default: null
    },
    errorType: {
      type: String,
      default: null
    },
    loginBannerSrc: {
      type: String,
      required: true
    }
  },
  data() {
    return {
      authorized: true
    };
  },
  computed: {
    submitUrl() {
      return urlFor('/oauth2/authorize/consent');
    }
  },
  methods: {
    onCancel() {
      this.authorized = false;
      this.onSubmit();
    },
    onSubmit() {
      this.$nextTick(() => {
        this.$refs.authorizeForm.$el.submit();
      });
    }
  }
};
