import { shouldNotExceedCharLength } from '@/core/util/validation/rules';
import CandidateActionLayout from '@/orangehrmRecruitmentPlugin/components/CandidateActionLayout';
import { APIService } from '@/core/util/services/api.service';
import { navigate } from '@/core/util/helper/navigation';
export default {
  components: {
    'candidate-action-layout': CandidateActionLayout
  },
  props: {
    candidateId: {
      type: Number,
      required: true
    }
  },
  setup(props) {
    const http = new APIService(window.appGlobal.baseUrl, `/api/v2/recruitment/candidates/${props.candidateId}/hire`);
    return {
      http
    };
  },
  data() {
    return {
      isLoading: false,
      note: null,
      rules: {
        note: [shouldNotExceedCharLength(2000)]
      }
    };
  },
  methods: {
    onSave() {
      this.isLoading = true;
      this.http.request({
        method: 'PUT',
        data: {
          note: this.note
        }
      }).then(() => {
        return this.$toast.updateSuccess();
      }).then(() => {
        navigate('/recruitment/addCandidate/{id}', {
          id: this.candidateId
        });
      });
    },
    onClickBack() {
      navigate('/recruitment/addCandidate/{id}', {
        id: this.candidateId
      });
    }
  }
};
