import { useInjectTableProps } from '@ohrm/oxd';
export default {
  name: 'ReviewPeriodCell',
  props: {
    header: {
      type: Object,
      required: true
    },
    reviewPeriodStart: {
      type: String,
      required: true
    },
    reviewPeriodEnd: {
      type: String,
      required: true
    }
  },
  setup() {
    const {
      screenState
    } = useInjectTableProps();
    return {
      screenState
    };
  },
  computed: {
    showHeader() {
      return !(this.screenState.screenType === 'lg' || this.screenState.screenType === 'xl');
    }
  }
};
