import { computed, ref } from 'vue';
import { OxdIcon, OxdSheet, OxdSpinner } from '@ohrm/oxd';
export default {
  name: 'BaseWidget',
  components: {
    'oxd-sheet': OxdSheet,
    'oxd-icon': OxdIcon,
    'oxd-loading-spinner': OxdSpinner
  },
  props: {
    icon: {
      type: String,
      required: true
    },
    title: {
      type: String,
      required: true
    },
    loading: {
      type: Boolean,
      default: false
    },
    empty: {
      type: Boolean,
      default: false
    },
    emptyText: {
      type: String,
      default: null
    },
    iconType: {
      type: String,
      default: undefined
    }
  },
  setup() {
    const widgetRef = ref();
    const defaultPic = `${window.appGlobal.publicPath}/images/dashboard_empty_widget_watermark.png`;
    const widgetBodyClasses = computed(() => ({
      'orangehrm-dashboard-widget-body': true,
      '--scroll-visible': widgetRef.value?.scrollHeight > widgetRef.value?.clientHeight
    }));
    return {
      widgetRef,
      defaultPic,
      widgetBodyClasses
    };
  }
};
